package stringoperation;

/**
 * @author Nasim Hasan
 *
 */
public class StringStr extends Operation{
	
	@Override
	public int lengthvalue(String arg) {
		// TODO Auto-generated method stub
		return arg.length();
	}

	@Override
	public String concatvalue(String arg1, String arg2) {
		// TODO Auto-generated method stub
		return arg1+arg2;
	}
	
	

}
